<?php

namespace heyAuto\DemoBundle\Entity;

use Doctrine\ORM\EntityRepository;
use heyAuto\DemoBundle\Entity\PosInvoice;
use heyAuto\DemoBundle\Entity\PosGroupMobile;
use heyAuto\DemoBundle\Entity\PosUserGroupMap;
use Symfony\Component\Validator\Constraints\EqualTo;
use Monolog\Logger;



/**
 * PosUserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PosInvoiceRepository extends EntityRepository
{
	
	public function getNumberRowForInvoice($companyCode) {
		return $this->getEntityManager()
		->createQuery(
				"SELECT inv
					FROM heyAutoDemoBundle:PosInvoice inv
					WHERE inv.company_code = '".$companyCode."'
					AND convert(varchar, inv.inv_starttime, 105) >= convert(varchar, getdate(), 105)
					AND inv.status != 3
					ORDER BY CASE inv.status WHEN 1 THEN 1 
											 WHEN 3 THEN 2
											 WHEN 0 THEN 3 
											 WHEN 2 THEN 4  
							 END, inv.inv_id DESC
				"
		)->getResult();
	}

	public function updateInvoiceByInvoiceCode(PosInvoice $posInvoice) {

		$manager = $this->getEntityManager();
		$manager->merge($posInvoice);
		$manager->flush();

		return array (
						'mSuccess' => true,
						'mErrorField' => null,
						'mMessage' => "User updated successfully"
				);
		
	}

	public function createNewPosInvoice(PosInvoice $posInvoice) 
	{
		
		if($posInvoice == null) {
			return 0;
			
		} else {
			
			$manager = $this->getEntityManager();
			$manager->persist($posInvoice);
			$manager->flush();
			
			return 1;
			
		}
	}

	public function findInvoiceIdByInvoiceCode($invCode, $companyCode)
	{
		// return $this->getEntityManager()->getRepository('heyAutoDemoBundle:PosInvoice')
		// 	->findOneBy(array('inv_code' => $invCode, 'company_code' => $companyCode));
		return $this->getEntityManager()
		->createQuery(
				" SELECT g
					FROM heyAutoDemoBundle:PosInvoice g
					WHERE g.inv_code = '".$invCode."' 
					AND g.company_code = '".$companyCode."' 
				"
		)->getResult();
	}

	public function getInvoiceByStatus($dateNow, $companyCode){
		 $sql = "SELECT inv.*
					FROM pos_invoice AS inv				
					WHERE inv.company_code = '".$companyCode."' 
					AND convert(varchar, inv.inv_starttime, 105) >= convert(varchar, getdate(), 105)
					AND inv.status != 3
					ORDER BY CASE inv.status WHEN 1 THEN 1 
											 WHEN 3 THEN 2
											 WHEN 0 THEN 3 
											 WHEN 2 THEN 4  
							 END, inv.inv_id DESC
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		$stmt->execute();
		return $stmt->fetchAll();
	}

	public function getInvoiceOfClient($from, $to, $companyCode){
		 $sql = " SELECT * 
					FROM (
					SELECT inv.inv_id, inv.inv_code, inv.total, inv.cost, inv.vat, inv.commision,
						   inv.inv_endtime, inv.inv_starttime, inv.user_id, inv.status, 
						   ROW_NUMBER() OVER (ORDER BY inv_id) AS RowNum
					FROM pos_invoice inv
					WHERE convert(varchar, inv.inv_starttime, 105)>= convert(varchar, getdate(), 105)
					AND inv.company_code = '".$companyCode."' AND inv.status = 1 OR inv.status = 4 
					) AS paping
					WHERE paping.RowNum BETWEEN '".$from."' and '".$to."'
					ORDER BY CASE paping.status WHEN 1 THEN 1 
											 WHEN 3 THEN 2
											 WHEN 0 THEN 3 
											 WHEN 2 THEN 4  
								 END, paping.inv_id DESC
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		$stmt->execute();
		return $stmt->fetchAll();
	}

	public function updatePriceInPosInvoice($inv_code, $total, $time_in){
		$sql = "UPDATE pos_invoice SET total = '".$total."', inv_endtime = '".$time_in."' WHERE inv_code = '".$inv_code."'
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		
		return $stmt->execute();
	}

	public function updateStatusAndEndTimeInPosInvoice($inv_code, $inv_type, $dateCurrent){
		$sql = "UPDATE pos_invoice SET status = 3 , inv_endtime='".$dateCurrent."', inv_type = '".$inv_type."' 
				where inv_code ='".$inv_code."'
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		
		return $stmt->execute();
	}

	public function updateStatusByInvoiceCodeAndCompanyCode($inv_code, $company_code, $time_in, $inv_type, $status){
		$sql = "UPDATE pos_invoice SET status = '".$status."' , inv_endtime= '".$time_in."',inv_type = '".$inv_type."' 
				where inv_code ='".$inv_code."' AND company_code = '".$company_code."'
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		
		return $stmt->execute();
	}

	public function reportInvoice($start_date, $end_date, $companyCode){
		 $sql = "SELECT CONVERT(VARCHAR(20),inv_endtime, 112) AS Mydate, SUM(total) AS summed, COUNT(inv_endtime) AS total
				 FROM pos_invoice WHERE company_code = '".$companyCode."'
				 GROUP BY CONVERT(VARCHAR(20),inv_endtime, 112) 
				 HAVING  CONVERT(VARCHAR(20),inv_endtime, 112)>= CONVERT(VARCHAR(20),'".$start_date."', 112) 
						 AND CONVERT(VARCHAR(20),inv_endtime, 112)<= CONVERT(VARCHAR(20),'".$end_date."', 112) 
						 AND CONVERT(VARCHAR(20),inv_endtime, 112) IS NOT NULL
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		$stmt->execute();
		return $stmt->fetchAll();
	}

	public function reportItemsInvoice($start_date, $end_date, $company_code, $language_code){
		 $sql = "SELECT p_inv_d.item_id , lng.lng_value,  
		 			SUM (p_inv_d.quantity ) as total, p_i_c_d.price
					FROM pos_invoice As p_inv
					INNER JOIN pos_invoice_detail as p_inv_d ON p_inv.inv_code = p_inv_d.inv_code
					INNER JOIN pos_items AS p_i ON p_i.item_id = p_inv_d.item_id
					INNER JOIN pos_items_cost_detail AS p_i_c_d ON p_i_c_d.item_id = p_i.item_id
					INNER JOIN pos_item_cost AS p_i_c ON p_i_c.id = p_i_c_d.items_cost_id
					INNER JOIN pos_language_item lng ON lng.item_id = p_i.item_id 
					WHERE p_inv.company_code = '".$company_code."'
					AND lng.lng_code = '".$language_code."'
					AND CONVERT(VARCHAR(20),p_i_c.start_date, 112)  <CONVERT(VARCHAR(20),'".$start_date."', 112) 
					AND  CONVERT(VARCHAR(20),p_i_c.end_date, 112) >CONVERT(VARCHAR(20),'".$end_date."', 112)
					AND CONVERT(VARCHAR(20),p_inv.inv_endtime, 112)>= CONVERT(VARCHAR(20),'".$start_date."', 112) 
					AND CONVERT(VARCHAR(20),p_inv.inv_endtime, 112)<= CONVERT(VARCHAR(20),'".$end_date."', 112)
					GROUP BY p_inv_d.item_id, lng.lng_value, p_i_c_d.price
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		$stmt->execute();
		return $stmt->fetchAll();
	}

	public function reportCategoryInvoice($start_date, $end_date, $company_code, $language_code, $category_id){
		 $sql = "SELECT p_inv_d.item_id ,  lng.lng_value,  SUM (p_inv_d.quantity ) as total, p_i_c_d.price
					FROM pos_invoice As p_inv
					INNER JOIN pos_invoice_detail as p_inv_d ON p_inv.inv_code = p_inv_d.inv_code
					INNER JOIN pos_items AS p_i ON p_i.item_id = p_inv_d.item_id
					INNER JOIN pos_items_cost_detail AS p_i_c_d ON p_i_c_d.item_id = p_i.item_id
					INNER JOIN pos_item_cost AS p_i_c ON p_i_c.id = p_i_c_d.items_cost_id
					INNER JOIN pos_language_item lng ON lng.item_id = p_i.item_id 
					INNER JOIN pos_category p_c ON p_c.category_id = p_i.category_id
					WHERE p_inv.company_code = '".$company_code."'
					AND lng.lng_code = '".$language_code."'
					AND  p_c.category_id = '".$category_id."'
					AND CONVERT(VARCHAR(20),p_i_c.start_date, 112)  <CONVERT(VARCHAR(20),'".$start_date."', 112) 
					AND  CONVERT(VARCHAR(20),p_i_c.end_date, 112) >CONVERT(VARCHAR(20),'".$end_date."', 112)
					AND CONVERT(VARCHAR(20),p_inv.inv_endtime, 112)>= CONVERT(VARCHAR(20),'".$start_date."', 112) 
					AND CONVERT(VARCHAR(20),p_inv.inv_endtime, 112)<= CONVERT(VARCHAR(20),'".$end_date."', 112)
					GROUP BY p_inv_d.item_id, lng.lng_value, p_i_c_d.price
				";
		$stmt = $this->getEntityManager()->getConnection()->prepare($sql);
		$stmt->execute();
		return $stmt->fetchAll();
	}

}
